(function() {
  var evalCode, fs;

  fs = require('fs');

  evalCode = function(code, preamble) {
    return eval("(function() {\n  var window = GLOBAL;\n  " + preamble + ";\n  return " + code + ";\n})();");
  };

  module.exports = {
    AWS: require('../../lib/aws'),
    Builder: require('../browser-builder'),
    chai: require('chai'),
    evalCode: evalCode
  };

}).call(this);
